INSERT INTO myportal_widget VALUES (1,'Calculette','Calculatrice simple',1,'html','s',1,1,1,'<script type=\"text/javascript\">\n 	<!--\n 	function verification(entree) {\n 	var seulement_ceci =\"0123456789[]()-+*%/.\";\n 	for (var i = 0; i < entree.length; i++)\n 	if (seulement_ceci.indexOf(entree.charAt(i))<0 ) return false;\n 	return true;\n 	}\n 	\n 	function resultat() {\n 	var x = 0;\n	if (verification(window.document.calculatrice.affiche.value))\n 	x = eval(window.document.calculatrice.affiche.value);\n 	window.document.calculatrice.affiche.value = x;\n 	}\n 	\n 	function ajouter(caracteres) {\n 	window.document.calculatrice.affiche.value =\n 	window.document.calculatrice.affiche.value + caracteres;\n 	}\n 	\n 	function fonction_speciale(fonction) {\n 	if (verification(window.document.calculatrice.affiche.value)) {\n 	if(fonction == \"sqrt\") {\n 	var x = 0;\n 	x = eval(window.document.calculatrice.affiche.value);\n 	window.document.calculatrice.affiche.value = Math.sqrt(x);\n 	}\n 	if(fonction == \"pow\") {\n 	var x = 0;\n 	x = eval(window.document.calculatrice.affiche.value);\n 	window.document.calculatrice.affiche.value = x * x;\n 	}\n 	if(fonction == \"log\") {\n 	var x = 0;\n 	x = eval(window.document.calculatrice.affiche.value);\n 	window.document.calculatrice.affiche.value = Math.log(x);\n 	}\n 	} else window.document.calculatrice.affiche.value = 0\n 	}\n 	//-->\n 	</script>\n	<style type=\"text/css\">\n 	.button { width:40px; text-align:center;\n 	font-family:System,sans-serif;\n 	font-size:100%; }\n 	.affiche { width:100%; text-align:right;\n 	font-family:System,sans-serif;\n 	font-size:100%; }\n 	</style>\n 	<form name=\"calculatrice\" action=\"\" onSubmit=\"resultat();return false;\">\n 	<table border=\"5\" cellpadding=\"10\" cellspacing=\"0\">\n 	<tr>\n 	<td bgcolor=\"#C0C0C0\">\n	<input type=\"text\" name=\"affiche\" align=\"right\" class=\"affiche\"></td>\n 	</tr><tr>\n 	<td bgcolor=\"#E0E0E0\">\n 	<table border=\"0\" cellpadding=\"0\" cellspacing=\"2\">\n 	<tr>\n 	<td><input type=\"button\" class=\"button\" value=\" 7 \" onClick=\"ajouter(\'7\')\"></td>\n 	<td><input type=\"button\" class=\"button\" value=\" 8 \" onClick=\"ajouter(\'8\')\"></td>\n 	<td><input type=\"button\" class=\"button\" value=\" 9 \" onClick=\"ajouter(\'9\')\"></td>\n 	<td><input type=\"button\" class=\"button\" value=\" + \" onClick=\"ajouter(\'+\')\"></td>\n 	</tr>\n 	<tr>\n 	<td><input type=\"button\" class=\"button\" value=\" 4 \" onClick=\"ajouter(\'4\')\"></td>\n 	<td><input type=\"button\" class=\"button\" value=\" 5 \" onClick=\"ajouter(\'5\')\"></td>\n 	<td><input type=\"button\" class=\"button\" value=\" 6 \" onClick=\"ajouter(\'6\')\"></td>\n 	<td><input type=\"button\" class=\"button\" value=\" - \" onClick=\"ajouter(\'-\')\"></td>\n 	</tr>\n 	<tr>\n 	<td><input type=\"button\" class=\"button\" value=\" 1 \" onClick=\"ajouter(\'1\')\"></td>\n 	<td><input type=\"button\" class=\"button\" value=\" 2 \" onClick=\"ajouter(\'2\')\"></td>\n 	<td><input type=\"button\" class=\"button\" value=\" 3 \" onClick=\"ajouter(\'3\')\"></td>\n 	<td><input type=\"button\" class=\"button\" value=\" * \" onClick=\"ajouter(\'*\')\"></td>\n 	</tr>\n 	<tr>\n 	<td><input type=\"button\" class=\"button\" value=\" = \" onClick=\"resultat()\"></td>\n 	<td><input type=\"button\" class=\"button\" value=\" 0 \" onClick=\"ajouter(\'0\')\"></td>\n 	<td><input type=\"button\" class=\"button\" value=\" . \" onClick=\"ajouter(\'.\')\"></td>\n 	<td><input type=\"button\" class=\"button\" value=\" / \" onClick=\"ajouter(\'/\')\"></td>\n 	</tr>\n 	<tr>\n 	<td><input type=\"button\" class=\"button\" value=\"sqrt \" onClick=\"fonction_speciale(\'sqrt\')\"></td>\n 	<td><input type=\"button\" class=\"button\" value=\" pow \" onClick=\"fonction_speciale(\'pow\')\"></td>\n 	<td><input type=\"button\" class=\"button\" value=\" log \" onClick=\"fonction_speciale(\'log\')\"></td>\n 	<td><input type=\"reset\" class=\"button\" value=\" C \"></td>\n 	</tr>\n 	</table>\n 	</td></tr></table>\n 	</form>\n'),(2,'Météo','Affichage des prévisions météorologiques',1,'html','dd',1,1,1,'<!-- Yahoo! Weather Badge -->\r\n<iframe allowtransparency=\"true\" marginwidth=\"0\" marginheight=\"0\" hspace=\"0\" vspace=\"0\" frameborder=\"0\" scrolling=\"no\" src=\"http://fr.meteo.yahoo.com/badge/?id=615702&u=c&t=trans&l=vertical\" height=\"255px\" width=\"260px\">\r\n	\r\n</iframe><noscript><a href=\"http://fr.meteo.yahoo.com/france/île-de-france/paris-615702/\">Paris Weather</a> </noscript>\r\n<!-- Yahoo! Weather Badge -->\r\n');
