INSERT INTO myportal_widget VALUES (1,'Calculette','Calculatrice simple',1,'html',2,'<script type=\"text/javascript\">\r\n 	<!--\r\n 	function verification(entree) {\r\n 	var seulement_ceci =\"0123456789[]()-+*%/.\";\r\n 	for (var i = 0; i < entree.length; i++)\r\n 	if (seulement_ceci.indexOf(entree.charAt(i))<0 ) return false;\r\n 	return true;\r\n 	}\r\n 	\r\n 	function resultat() {\r\n 	var x = 0;\r\n	if (verification(window.document.calculatrice.affiche.value))\r\n 	x = eval(window.document.calculatrice.affiche.value);\r\n 	window.document.calculatrice.affiche.value = x;\r\n 	}\r\n 	\r\n 	function ajouter(caracteres) {\r\n 	window.document.calculatrice.affiche.value =\r\n 	window.document.calculatrice.affiche.value + caracteres;\r\n 	}\r\n 	\r\n 	function fonction_speciale(fonction) {\r\n 	if (verification(window.document.calculatrice.affiche.value)) {\r\n 	if(fonction == \"sqrt\") {\r\n 	var x = 0;\r\n 	x = eval(window.document.calculatrice.affiche.value);\r\n 	window.document.calculatrice.affiche.value = Math.sqrt(x);\r\n 	}\r\n 	if(fonction == \"pow\") {\r\n 	var x = 0;\r\n 	x = eval(window.document.calculatrice.affiche.value);\r\n 	window.document.calculatrice.affiche.value = x * x;\r\n 	}\r\n 	if(fonction == \"log\") {\r\n 	var x = 0;\r\n 	x = eval(window.document.calculatrice.affiche.value);\r\n 	window.document.calculatrice.affiche.value = Math.log(x);\r\n 	}\r\n 	} else window.document.calculatrice.affiche.value = 0\r\n 	}\r\n 	//-->\r\n 	</script>\r\n	<style type=\"text/css\">\r\n 	.button { width:40px; text-align:center;\r\n 	font-family:System,sans-serif;\r\n 	font-size:100%; }\r\n 	.affiche { width:100%; text-align:right;\r\n 	font-family:System,sans-serif;\r\n 	font-size:100%; }\r\n 	</style>\r\n 	<form name=\"calculatrice\" action=\"\" onSubmit=\"resultat();return false;\">\r\n 	<table border=\"5\" cellpadding=\"10\" cellspacing=\"0\">\r\n 	<tr>\r\n 	<td bgcolor=\"#C0C0C0\">\r\n	<input type=\"text\" name=\"affiche\" align=\"right\" class=\"affiche\"></td>\r\n 	</tr><tr>\r\n 	<td bgcolor=\"#E0E0E0\">\r\n 	<table border=\"0\" cellpadding=\"0\" cellspacing=\"2\">\r\n 	<tr>\r\n 	<td><input type=\"button\" class=\"button\" value=\" 7 \" onClick=\"ajouter(\'7\')\"></td>\r\n 	<td><input type=\"button\" class=\"button\" value=\" 8 \" onClick=\"ajouter(\'8\')\"></td>\r\n 	<td><input type=\"button\" class=\"button\" value=\" 9 \" onClick=\"ajouter(\'9\')\"></td>\r\n 	<td><input type=\"button\" class=\"button\" value=\" + \" onClick=\"ajouter(\'+\')\"></td>\r\n 	</tr>\r\n 	<tr>\r\n 	<td><input type=\"button\" class=\"button\" value=\" 4 \" onClick=\"ajouter(\'4\')\"></td>\r\n 	<td><input type=\"button\" class=\"button\" value=\" 5 \" onClick=\"ajouter(\'5\')\"></td>\r\n 	<td><input type=\"button\" class=\"button\" value=\" 6 \" onClick=\"ajouter(\'6\')\"></td>\r\n 	<td><input type=\"button\" class=\"button\" value=\" - \" onClick=\"ajouter(\'-\')\"></td>\r\n 	</tr>\r\n 	<tr>\r\n 	<td><input type=\"button\" class=\"button\" value=\" 1 \" onClick=\"ajouter(\'1\')\"></td>\r\n 	<td><input type=\"button\" class=\"button\" value=\" 2 \" onClick=\"ajouter(\'2\')\"></td>\r\n 	<td><input type=\"button\" class=\"button\" value=\" 3 \" onClick=\"ajouter(\'3\')\"></td>\r\n 	<td><input type=\"button\" class=\"button\" value=\" * \" onClick=\"ajouter(\'*\')\"></td>\r\n 	</tr>\r\n 	<tr>\r\n 	<td><input type=\"button\" class=\"button\" value=\" = \" onClick=\"resultat()\"></td>\r\n 	<td><input type=\"button\" class=\"button\" value=\" 0 \" onClick=\"ajouter(\'0\')\"></td>\r\n 	<td><input type=\"button\" class=\"button\" value=\" . \" onClick=\"ajouter(\'.\')\"></td>\r\n 	<td><input type=\"button\" class=\"button\" value=\" / \" onClick=\"ajouter(\'/\')\"></td>\r\n 	</tr>\r\n 	<tr>\r\n 	<td><input type=\"button\" class=\"button\" value=\"sqrt \" onClick=\"fonction_speciale(\'sqrt\')\"></td>\r\n 	<td><input type=\"button\" class=\"button\" value=\" pow \" onClick=\"fonction_speciale(\'pow\')\"></td>\r\n 	<td><input type=\"button\" class=\"button\" value=\" log \" onClick=\"fonction_speciale(\'log\')\"></td>\r\n 	<td><input type=\"reset\" class=\"button\" value=\" C \"></td>\r\n 	</tr>\r\n 	</table>\r\n 	</td></tr></table>\r\n 	</form>\r\n',0,2,0,0),(2,'Météo','Affichage des prévisions météorologiques',1,'html',3,'<!-- Yahoo! Weather Badge -->\r\n<iframe allowtransparency=\"true\" marginwidth=\"0\" marginheight=\"0\" hspace=\"0\" vspace=\"0\" frameborder=\"0\" scrolling=\"no\" src=\"http://fr.meteo.yahoo.com/badge/?id=615702&u=c&t=trans&l=vertical\" height=\"255px\" width=\"260px\">\r\n	\r\n</iframe><noscript><a href=\"http://fr.meteo.yahoo.com/france/île-de-france/paris-615702/\">Paris Weather</a> </noscript>\r\n<!-- Yahoo! Weather Badge -->\r\n',0,2,0,0),(3,'Calendrier','Calendrier',1,'html',6,'<!-- Google Calendar Element Code -->\r\n<iframe frameborder=0 marginwidth=0 marginheight=0 border=0 style=\"border:0;margin:0;width:260px;height:300px;\" src=\"http://www.google.com/calendar/embed?showTitle=0&showTabs=0&showPrint=0&showCalendars=0&wkst=1&element=true&src=usa__en%40holiday.calendar.google.com\" scrolling=\"no\" allowtransparency=\"true\"></iframe>',0,2,0,0),(4,'Google','Recherche Google',1,'html',5,'<SCRIPT LANGUAGE=JAVASCRIPT>\r\nfunction googleSearch() {\r\n  if (document.doingasearch.searchbox.value!=\"\") {    \r\n      window.open(\"http://www.google.com/search?hl=en&q=\" + document.doingasearch.searchbox.value) ;        \r\n  }\r\n  else {\r\n    alert(\"Veuillez saisir un texte de recherche.\")\r\n    document.doingasearch.searchbox.focus();\r\n  }\r\n}\r\n</SCRIPT>\r\n  <FORM name=\"doingasearch\" id=\"doingasearch\">\r\n   Google  \r\n  <INPUT name=searchbox type=text size=20>\r\n  <INPUT type=\"button\" value=\"Rechercher\" onclick=\"return googleSearch();\">\r\n</FORM>',0,2,1,0),(5,'Google Maps','Google Maps',1,'html',8,'<!-- Google Maps Element Code -->\r\n<iframe frameborder=0 marginwidth=0 marginheight=0 border=0 style=\"border:0;margin:0;width:260px;height:250px;\" src=\"http://www.google.com/uds/modules/elements/mapselement/iframe.html?maptype=hybrid&latlng=48.856667%2C2.350987&mlatlng=48.856667%2C2.350987&maddress1=Paris&maddress2=France&zoom=12&mtitle=Paris&element=true\" scrolling=\"no\" allowtransparency=\"true\"></iframe>',0,2,0,0),(6,'Exemple widget fixe','Widget non supprimable et non repliable',2,'html',1,'<p>Certains widgets, comme celui-ci, peuvent être définis comme étant non supprimables et non repliables. \r\n</p>\r\n<p>Par ailleurs une ou plusieurs colonnes peuvent être définies comme ayant des widgets non déplaçables comme celle-ci.\r\n</p>',1,1,0,0),(7,'Horloge','Horloge analogique',1,'html',4,'<!--[if IE]><script type=\"text/javascript\" src=\"js/plugins/myportal/excanvas.js\"></script><![endif]--> <script type=\"text/javascript\" src=\"js/plugins/myportal/coolclock.js\"></script> <script type=\"text/javascript\" src=\"js/plugins/myportal/moreskins.js\"></script>\r\n\r\n<center>\r\n<canvas id=\"clock1\" class=\"CoolClock:classic\"></canvas>\r\n</center>',0,2,0,1);
